import React, { useState } from 'react';

const SentContent = ({ emails }) => {
  const [selectedEmail, setSelectedEmail] = useState(null);

  const handleEmailClick = (index) => {
    setSelectedEmail(index);
  };

  const renderEmailSummary = (email, index) => (
    <div key={index} onClick={() => handleEmailClick(index)} className="email-summary">
      <p>Do: {email.recipient}</p>
      <p>Data: {new Date(email.sentDate).toLocaleString()}</p>
      <p>Temat: {email.subject}</p>
    </div>
  );

  return (
    <div className="content-container">
      <div className="email-list">
        {emails.map((email, index) => renderEmailSummary(email, index))}
      </div>
      <div className="email-details">
        {selectedEmail !== null ? (
          <div className="email-details-content">
            <h2>Wiadomość</h2>
            <p>Do: {emails[selectedEmail].recipient}</p>
            <p>Data: {new Date(emails[selectedEmail].sentDate).toLocaleString()}</p>
            <p>Temat: {emails[selectedEmail].subject}</p>
            <p>Treść:<br/> {emails[selectedEmail].content}</p>
          </div>
        ) : (
          <div className="no-email-selected">
            Wybierz wiadomość, aby wyświetlić zawartość
          </div>
        )}
      </div>
    </div>
  );
};

export default SentContent;
