import React, { useState } from 'react';
import "./InboxContent.css"

const InboxContent = ({ emails }) => {
  const [selectedEmail, setSelectedEmail] = useState(null);

  const handleEmailClick = (index) => {
    setSelectedEmail(index);
  };

  const renderEmailSummary = (email, index) => {
    const sentDate = new Date(email.sentDate); // Konwertujemy string na obiekt Date
    const formattedDate = sentDate.toLocaleString(); // Formatujemy datę
  
    return (
      <div key={index} onClick={() => handleEmailClick(index)} className="email-summary">
        <p>Od: {email.sender}</p>
        <p>Data: {formattedDate}</p>
        <p>Temat: {email.subject}</p>
      </div>
    );
  };

  return (
    <div className="content-container">
      <div className="email-list">
        {emails.map((email, index) => renderEmailSummary(email, index))}
      </div>
      <div className="email-details">
        {selectedEmail !== null && (
          <div className="email-details-content">
            <h2>Wiadomość</h2>
            <p>Od: {emails[selectedEmail].sender}</p>
            <p>Data wysłania: {formattedDate}</p>
            {formattedDate && <p>Data: {formattedDate}</p>}
            <p>Temat: {emails[selectedEmail].subject}</p>
            <p>Treść: {emails[selectedEmail].content}</p>
          </div>
        )}
      </div>
    </div>
  );
};

export default InboxContent;
