import React,{useState, useEffect} from 'react';
import './GeneralPage.css';
import InboxContent from './InboxContent.js';
import MailContent from './MailContent.js'
import SentContent from './SentContent.js';
import TrashContent from './TrashContent.js';
import DrafContent from './DraftContent.js';

const GeneralPage = () => {
    const [selectedMenu, setSelectedMenu] = useState('inbox');
    const [emails, setEmails] = useState([]);
    const [unsentEmails, setUnsentEmails] = useState([]);
    const email = localStorage.getItem("email");
    const loggedIn = localStorage.getItem("loggedIn");
    const [user, setUser] = useState([]);

    const handleMenuClick = (menu) => {
        setSelectedMenu(menu);
        getEmails(menu === 'inbox' ?  'recipient' : 'sender');
    }
    
    useEffect(() => {
        if (loggedIn === 'true') {
            getUserData();
            getEmails(selectedMenu === 'inbox' ?  'recipient' : 'sender');
        }
        // eslint-disable-next-line react-hooks/exhaustive-deps
    }, []);

    const getUserData = async () => {
        try {
            const response = await fetch(`http://localhost:8080/api/users/${email}`);
            
            if (!response.ok) {
                throw new Error(`HTTP error! Status: ${response.status}`);
            }
    
            setUser(await response.json());    
        } catch (error) {
            console.error('Fetch error:', error);
        }
    };

    const getEmails = async (who) => {
        try {
            const response = await fetch(`http://localhost:8080/api/mails/${who}?${who}=${email}`);
            if (!response.ok) {
                throw new Error(`HTTP error! Status: ${response.status}`);
            }
            const data = await response.json();
            setEmails(data);
        } catch (error) {
            console.error('Fetch error:', error);
        }
    };

    const getUnsentMails = async (who) => {
        try {
            const response = await fetch(`http://localhost:8080/api/unsentMails/${who}?${who}=${email}`);
            if (!response.ok) {
                throw new Error(`HTTP error! Status: ${response.status}`);
            }
            const data = await response.json();
            setUnsentEmails(data);
        } catch (error) {
            console.error('Fetch error:', error);
        }
    };

    const renderContent = () => {
        switch (selectedMenu) {
            case 'inbox':
                return <InboxContent emails={emails} />;
            case 'sent':
                return <SentContent emails={emails} />;
            case 'draft':
                return <DrafContent onClick={getUnsentMails} emails={emails}/>;
            case 'spam':
                return <div className="content-container">Zawartość spamu</div>;
            case 'trash':
                return <TrashContent />;
            case 'mail':
                return <MailContent email={email}/>;
            default:
                return null;
        }
    };

    const handleLogout = () => {
      localStorage.removeItem('loggedIn');
      localStorage.removeItem('email');
      localStorage.removeItem('password');
    
      
      window.location.href = '../login';
    };

    const centerStyle = {
        width: '90%'
    };

    return (
        <div className="container">
            <div className="header-container" id='style'>
                <div className="logo-container">
                    <img src={require('../logo.png')} alt="logo" />
                    <h1>Fast</h1>
                </div>
                <div className="header-right">
                    <h3>Witaj, {user.name} {user.lastname}! </h3>
                </div>
                <div className='form-buttons'>
                    <button onClick={handleLogout}>Wyloguj</button>
                </div>
            </div>
            <div className="aside-content-container registration-form" id='style' style={centerStyle}>
                <div className="aside-container">
                    <div className="inbox" onClick={() => handleMenuClick('inbox')}>Skrzynka odbiorcza</div>
                    <div className="sent" onClick={() => handleMenuClick('sent')}>Wysłane</div>
                    <div className="draft" onClick={() => handleMenuClick('draft')}>Kopia robocza</div>
                    <div className="spam" onClick={() => handleMenuClick('spam')}>Spam</div>
                    <div className="trash" onClick={() => handleMenuClick('trash')}>Kosz</div>
                    <div className="mail" onClick={() => handleMenuClick('mail')}>Nowa wiadomość</div>
                </div>
                <div className="content-container">
                    {renderContent()}
                </div>
            </div>
        </div>
    );
}

export default GeneralPage;
