import React, { useState } from 'react';
import "./InboxContent.css"

const DrafContent = ({ emails }) => {
  const [selectedEmail, setSelectedEmail] = useState(null);
  const [hideEmailList, setHideEmailList] = useState(false);
  
  useEffect(() => {
    if (hideEmailList) {
      // Perform actions when hideEmailList changes
      // For example, you can fetch data, update other states, etc.
    }
  }, [hideEmailList]);

    
  const handleEmailClick = (index) => {
    setSelectedEmail(index);
  };



  const renderEmailSummary = (email, index) => (
    <div key={index} onClick={() => handleEmailClick(index)} className="email-summary">
      <p>{new Date(email.savedDate).toLocaleDateString()} {new Date(email.savedDate).toLocaleTimeString([], { hour: 'numeric', minute: 'numeric' })}</p>
      <p>Do: {email.recipient}</p>
      <p>Temat: {email.subject}</p>
    </div>
  );

  const handleEdit = () => {
    setHideEmailList(true);
  }


  return (
    <div className="content-container">
      <div className={hideEmailList ? "email-list hidden" : "email-list"}>
        {emails.map((email, index) => renderEmailSummary(email, index))}
      </div>
      <div className={hideEmailList ? "email-details full-width" : "email-details"}>
        {selectedEmail !== null ? (
          <div className="email-details-content">
            <h2>Wiadomość</h2>
            <div className='email-details-content-field'>{new Date(emails[selectedEmail].savedDate).toLocaleDateString()} {new Date(emails[selectedEmail].savedDate).toLocaleTimeString([], { hour: 'numeric', minute: 'numeric' })}</div>
            <div className='email-details-content-field'>Do: {emails[selectedEmail].recipient}</div>
            <div className='email-details-content-field'>Temat: {emails[selectedEmail].subject}</div>
            <div className='email-details-content-field'>Treść:<br/> {emails[selectedEmail].content}</div>
            <div className='form-buttons'>
                <input type="submit" value="Wyślij" />
                <button type="button" onClick={handleEdit()}>Edytuj</button>
            </div>
          </div>
        ) : (
          <div className="no-email-selected">
            Wybierz wiadomość, aby wyświetlić zawartość
          </div>
        )}
      </div>
    </div>
  );
};

export default DrafContent;
